\documentclass[a4paper,11pt]{article} \usepackage[utf8]{inputenc} \usepackage[T1]{fontenc} \usepackage{amsmath,amssymb} \usepackage{enumitem} \usepackage{hyperref} \usepackage{geometry} \geometry{margin=1in}
\title{\textbf{\textit{Summary of ``Quantum-limit Chern Topological Magnetism in TbMn\textsubscript{6}Sn\textsubscript{6}&rsquo;&rsquo;}}} \author{\vspace{5mm}\Kevin Y. Lin\vspace{5mm}\\textit{Freshman, Southern University of Science and Technology (SUSTech)}\\textit{Shenzhen, Guangdong, China}} \date{(7/8/2025)}
\begin{document} \maketitle
\section*{Background} In Kagome materials with strong spin-orbit coupling and out-of-plane magnetization, spin-polarized Dirac fermions are theoretically possible, analogous to the Haldane model. However, ideal quantum materials are lacking, posing experimental challenges.
\section*{Key Findings / Evidence of Quantum-limit Chern Magnet}
\subsection*{Spin-polarized Dirac cones with Landau Quantization} \begin{enumerate}[label=(\arabic*), leftmargin=*] \item Zero-field STM peak moves linearly with magnetic field (Zeeman effect): [ \Delta E = \pm \tfrac{1}{2} g \mu_B B. ] Indicates spin-polarized electronic states.
\item STM (dI/dV) maps at high magnetic field show discrete Landau quantization, observing clear Dirac dispersion and characteristic (E_n) vs. (B) dependence.
\item ARPES measurements confirm Dirac fermions in the Brillouin Zone. \end{enumerate}
\subsection*{Chern Gap} \begin{enumerate}[label=(\arabic*), leftmargin=*] \item \emph{Landau fan:}
High-bias STM measurements under varying magnetic fields show two parallel straight lines, revealing a Chern gap.
\item \emph{Landau-level fitting formula:} [ E_n(B) = \pm \sqrt{\left(\frac{\Delta}{2}\right)^2 + 2 e \hbar v_F^2 |n| B} - \frac{1}{2}g \mu_B B, ] with fitted parameters: [ \Delta = 34\pm2,\mathrm{meV},\quad E_D = 130,\mathrm{meV},\quad v_F = 4.2\times 10^5,\mathrm{m/s}. ] \end{enumerate}
\subsection*{Bulk-boundary Correspondence and Dissipationless Edge States} \begin{itemize}[leftmargin=*] \item STM (dI/dV) mapping (Fig. 4a) clearly demonstrates localized in-gap edge states at step edges (interfaces between Kagome lattice layers).
\item Edge states appear due to topological properties, as the bulk is insulating: [ n_{\text{edge}} = C,(\text{Chern number}) ]
\item Authors conducted an experiment, revealing dissipationless edge states by low QPI peaks (Fourier transforms) in Fig.~4b. \end{itemize}
\subsection*{Quantum Limit} \begin{enumerate}[label=(\arabic*), leftmargin=*] \item \textbf{Low carrier concentration:}
The Fermi level is very close to the Dirac point, leading to extremely low carrier density and symmetric electron-hole excitations \cite{PRX2024}. \item \textbf{Discrete and sparse Landau levels (Fig. 2b).} \end{enumerate}
\subsection*{Anomalous Hall Effect (AHE)} \begin{itemize}[leftmargin=*] \item AHE arises due to intrinsic Berry curvature: [ \rho_{xy}^{\mathrm{AHE}} = \alpha \rho_{xx} + \beta \rho_{xx}^2 + \gamma \rho_{xy}^2. ]
\item Experimentally observed strong linear relationship between (\rho_{xy}^{\mathrm{AHE}}) and (\rho_{xx}^2), confirming intrinsic Berry curvature dominance.
\item Verification of Chern gap via Berry curvature: [ \sigma_{xy} = -\frac{e^2}{h}\frac{1}{N}\sum_{n}\int_{\mathrm{BZ}}\frac{d^2k}{(2\pi)^2} f_n(\mathbf{k})\Omega_n(\mathbf{k}), ] Substituting (\Delta \approx 34,\mathrm{meV}) and (E_D \approx 130,\mathrm{meV}) gives theoretical (\sigma_{xy}^{\mathrm{Berry}} = 0.13, e^2/h), matching experimentally measured (0.14, e^2/h), confirming Berry curvature origin. \end{itemize}
\section*{Research Significance} \begin{itemize}[leftmargin=*] \item First experimental evidence of a ``quantum-limited Chern topological magnet&rsquo;&rsquo; \cite{Nature2020}.
\item Establishes an experimental framework demonstrating bulk-boundary-Berry correspondence in topological magnets. \end{itemize}
\section*{References} \begin{itemize}[leftmargin=*]
\item Physical Review X \textbf{14}, 011047 (2024). \url{https://doi.org/10.1103/PhysRevX.14.011047}
\item Yin, J.-X., Ma, W., Cochran, T. A., et al. (2020). Quantum-limit Chern topological magnetism in TbMn\textsubscript{6}Sn\textsubscript{6}. \textit{Nature}, 583, 533–536.\url{https://doi.org/10.1038/s41586-020-2482-7} \end{itemize}
\end{document}
